DROP TABLE IF EXISTS `bibs_data_table`;
CREATE TABLE `bibs_data_table` (
  `dbsid` varchar(5) NOT NULL,
  `name` varchar(143) DEFAULT NULL,
  `strasse` varchar(56) DEFAULT NULL,
  `plz` int(5) DEFAULT NULL,
  `ort` varchar(23) DEFAULT NULL,
  `vorwahl` varchar(9) DEFAULT NULL,
  `tel` varchar(47) DEFAULT NULL,
  `url` varchar(107) DEFAULT NULL,
  `opac` varchar(101) DEFAULT NULL,
  `öffnungszeiten` varchar(254) DEFAULT NULL,
  `bestandsgrößenklasse` varchar(21) DEFAULT NULL,
  `unterhaltsträger` varchar(64) DEFAULT NULL,
  `dbv` varchar(38) DEFAULT NULL,
  `leitung` varchar(12) DEFAULT NULL,
  `pt` point DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


ALTER TABLE `bibs_data_table`
  ADD PRIMARY KEY (`dbsid`),
  ADD UNIQUE KEY `dbsid` (`dbsid`);